mapper.select:
SELECT D.DNO, D.MNO AS CUSTOMER_NO, M.ID, M.NAME, M.NICKNAME, M.TEL, M.ADDRESS, M.POST_NO, D.RIDER_ID, (SELECT NAME FROM MEMBER WHERE MNO=D.RIDER_ID) AS RIDER_NAME, D.REG_ID AS SELLER_ID, (SELECT NAME FROM MEMBER WHERE MNO=D.REG_ID) AS SELLER_NAME, (SELECT TEL FROM MEMBER WHERE MNO=D.REG_ID) AS SELLER_TEL, (SELECT POST_NO FROM MEMBER WHERE MNO=D.REG_ID) AS SELLER_POST_NO, (SELECT ADDRESS FROM MEMBER WHERE MNO=D.REG_ID) AS SELLER_ADDRESS, D.COURIER, D.PRODUCT_NAME, D.TRACKING_NO, D.DELIVERY_STATE, D.DELIVERY_SCH_TIME, D.DELIVERY_TIME, D.RECEIVED_TIME, D.RECEIVED_YN, D.RETURN_YN, D.SENDING_YN, D.REG_DATE FROM DELIVERY D LEFT JOIN MEMBER M ON D.MNO = M.MNO
${type} = #{no}
AND D.DELIVERY_SCH_TIME = #{schTime}

mapper.update:
update DELIVERY
set TRACKING_NO = #{trackingNo}
where DNO =  #{dno}

UPDATE DELIVERY
SET RETURN_YN = 'Y'
WHERE DNO = #{dno}

UPDATE DELIVERY
SET SENDING_YN = 'Y'
WHERE DNO = #{dno}
